      .addSlider(slider => slider
        .setLimits(1, 10, 1)
        .setValue(this.plugin.settings.maxSuggestionsPerNote)
        .setDynamicTooltip()
        .onChange(async (value) => {
          this.plugin.settings.maxSuggestionsPerNote = value;
          await this.plugin.saveSettings();
        }));

    new Setting(containerEl)
      .setName('Create backlinks')
      .setDesc('When adding a link to an isolated note, also add a backlink from the target note')
      .addToggle(toggle => toggle
        .setValue(this.plugin.settings.createBacklinks)
        .onChange(async (value) => {
          this.plugin.settings.createBacklinks = value;
          await this.plugin.saveSettings();
        }));

    new Setting(containerEl)
      .setName('Use AI for relevance analysis')
      .setDesc('Use AI to analyze note relevance (requires Vibesidian plugin)')
      .addToggle(toggle => toggle
        .setValue(this.plugin.settings.useAIForRelevance)
        .onChange(async (value) => {
          this.plugin.settings.useAIForRelevance = value;
          await this.plugin.saveSettings();
        }));

    // Automation Settings Section
    containerEl.createEl('h3', { text: 'Automation Settings' });

    new Setting(containerEl)
      .setName('Scan on startup')
      .setDesc('Automatically scan for isolated notes when Obsidian starts')
      .addToggle(toggle => toggle
        .setValue(this.plugin.settings.scanOnStartup)
        .onChange(async (value) => {
          this.plugin.settings.scanOnStartup = value;
          await this.plugin.saveSettings();
        }));

    new Setting(containerEl)
      .setName('Auto-trigger on save')
      .setDesc('Automatically analyze and suggest connections when a new note is saved')
      .addToggle(toggle => toggle
        .setValue(this.plugin.settings.autoTriggerOnSave)
        .onChange(async (value) => {
          this.plugin.settings.autoTriggerOnSave = value;
          await this.plugin.saveSettings();
        }));

    // Invariants Settings Section
    containerEl.createEl('h3', { text: 'Invariants Checking' });

    new Setting(containerEl)
      .setName('Check invariants')
      .setDesc('Check if new notes adhere to the 5 invariants principles')
      .addToggle(toggle => toggle
        .setValue(this.plugin.settings.checkInvariants)
        .onChange(async (value) => {
          this.plugin.settings.checkInvariants = value;
          await this.plugin.saveSettings();
        }));

    new Setting(containerEl)
      .setName('Invariants file path')
      .setDesc('Path to the file containing the 5 invariants principles')
      .addText(text => text
        .setPlaceholder('3-Resources/0_Invariants.md')
        .setValue(this.plugin.settings.invariantsFilePath)
        .onChange(async (value) => {
          this.plugin.settings.invariantsFilePath = value;
          await this.plugin.saveSettings();
        }));
  }
}

import { App, TFile, Notice } from 'obsidian';
import { InvariantViolation } from './types';

export class InvariantChecker {
  private app: App;
  private invariantsFilePath: string;
  private invariants: Array<{
    number: number;
    name: string;
    principle: string;
    description: string;
  }> = [];
  private invariantsLoaded: boolean = false;

  constructor(app: App, invariantsFilePath: string) {
    this.app = app;
    this.invariantsFilePath = invariantsFilePath;
  }

  /**
   * Loads the invariants from the specified file
   * @returns Promise<boolean> Success status
   */
  async loadInvariants(): Promise<boolean> {
    try {
      // Get the invariants file
      const invariantsFile = this.app.vault.getAbstractFileByPath(this.invariantsFilePath);

      if (!invariantsFile || !(invariantsFile instanceof TFile)) {
        console.error(`Invariants file not found at ${this.invariantsFilePath}`);
        return false;
      }

      // Read the file content
      const content = await this.app.vault.read(invariantsFile as TFile);

      // Parse the invariants
      this.parseInvariants(content);
      this.invariantsLoaded = true;

      return true;
    } catch (error) {
      console.error('Error loading invariants:', error);
      return false;
    }
  }

  /**
   * Parses the invariants from the file content
   * @param content The content of the invariants file
   */
  private parseInvariants(content: string): void {
    this.invariants = [];

    // Regular expression to match invariant sections
    // Looks for "## 불변량 X: Name" pattern
    const invariantRegex = /## 불변량 (\d+): ([^\n]+)[\s\S]*?### 원칙\s*\n\s*\*\*"([^"]+)"\*\*\s*\n\s*([^\n]+)/g;

    let match;
    while ((match = invariantRegex.exec(content)) !== null) {
      const number = parseInt(match[1]);
      const name = match[2].trim();
      const principle = match[3].trim();
      const description = match[4].trim();

      this.invariants.push({
        number,
        name,
        principle,
        description
      });
    }

    console.log(`Loaded ${this.invariants.length} invariants from ${this.invariantsFilePath}`);
  }

  /**
   * Checks if a note adheres to the invariants
   * @param noteContent The content of the note to check
   * @returns Promise<InvariantViolation[]> Array of violations, empty if none
   */
  async checkNoteAgainstInvariants(noteContent: string): Promise<InvariantViolation[]> {
    if (!this.invariantsLoaded) {
      const loaded = await this.loadInvariants();
      if (!loaded) {
        return [{
          invariantNumber: 0,
          invariantName: "Error",
          description: "Failed to load invariants",
          violationReason: "Could not load invariants file",
          suggestion: `Check that the invariants file exists at ${this.invariantsFilePath}`
        }];
      }
    }

    const violations: InvariantViolation[] = [];

    // Check each invariant
    for (const invariant of this.invariants) {
      const violation = this.checkInvariant(invariant, noteContent);
      if (violation) {
        violations.push(violation);
      }
    }

    return violations;
  }

  /**
   * Checks a specific invariant against a note
   * @param invariant The invariant to check
   * @param noteContent The content of the note
   * @returns InvariantViolation | null Violation if found, null otherwise
   */
  private checkInvariant(
    invariant: { number: number; name: string; principle: string; description: string },
    noteContent: string
  ): InvariantViolation | null {
    // Use AI to check if the note adheres to the invariant
    try {
      const vibesidian = this.app.plugins.getPlugin('vibesidian');

      if (!vibesidian) {
        console.error('Vibesidian plugin not found');
        return null;
      }

      // For now, we'll use a simpler approach without AI
      // In the future, this could be enhanced with AI analysis
      const violation = this.simpleInvariantCheck(invariant, noteContent);
      return violation;
    } catch (error) {
      console.error('Error checking invariant:', error);
      return null;
    }
  }

  /**
   * Simple invariant check based on keywords and patterns
   * @param invariant The invariant to check
   * @param noteContent The content of the note
   * @returns InvariantViolation | null Violation if found, null otherwise
   */
  private simpleInvariantCheck(
    invariant: { number: number; name: string; principle: string; description: string },
    noteContent: string
  ): InvariantViolation | null {
    const contentLower = noteContent.toLowerCase();

    // Check based on invariant number
    switch (invariant.number) {
      case 1: // 복잡성 단순화 (Simplicity Over Complexity)
        // Check if the note is too complex (e.g., too many sections, long paragraphs)
        const sections = noteContent.split(/#{2,}/g).length - 1;
        const avgParagraphLength = this.calculateAvgParagraphLength(noteContent);

        if (sections > 10 || avgParagraphLength > 200) {
          return {
            invariantNumber: invariant.number,
            invariantName: invariant.name,
            description: invariant.principle,
            violationReason: `Note appears too complex (${sections} sections, avg paragraph length: ${avgParagraphLength.toFixed(0)} chars)`,
            suggestion: "Consider simplifying the note by breaking it down into smaller, more focused notes or reducing complexity."
          };
        }
        break;

      case 2: // 데이터 기반 객관성 (Data-Driven Objectivity)
        // Check if the note contains subjective language without data backing
        const subjectiveTerms = ['I think', 'I believe', 'in my opinion', 'feel like', 'seems like'];
        const dataTerms = ['data', 'evidence', 'research', 'study', 'analysis', 'statistics', 'measured', 'quantitative'];

        const hasSubjectiveTerms = subjectiveTerms.some(term => contentLower.includes(term.toLowerCase()));
        const hasDataTerms = dataTerms.some(term => contentLower.includes(term.toLowerCase()));

        if (hasSubjectiveTerms && !hasDataTerms) {
          return {
            invariantNumber: invariant.number,
            invariantName: invariant.name,
            description: invariant.principle,
            violationReason: "Note contains subjective language without data backing",
            suggestion: "Add data, evidence, or research to support your claims and make them more objective."
          };
        }
        break;

      case 3: // 구조적 사고 (Root Cause Focus)
        // Check if the note addresses symptoms rather than root causes
        const symptomTerms = ['symptom', 'quick fix', 'workaround', 'temporary solution'];
        const rootCauseTerms = ['root cause', 'underlying', 'fundamental', 'structure', 'system', 'feedback loop'];

        const hasSymptomTerms = symptomTerms.some(term => contentLower.includes(term.toLowerCase()));
        const hasRootCauseTerms = rootCauseTerms.some(term => contentLower.includes(term.toLowerCase()));

        if (hasSymptomTerms && !hasRootCauseTerms) {
          return {
            invariantNumber: invariant.number,
            invariantName: invariant.name,
            description: invariant.principle,
            violationReason: "Note focuses on symptoms rather than root causes",
            suggestion: "Focus on identifying and addressing the underlying structural issues rather than just treating symptoms."
          };
        }
        break;

      case 4: // 점진적 개선 (Continuous Learning)
        // Check if the note presents a static solution without room for improvement
        const staticTerms = ['final solution', 'perfect answer', 'complete solution', 'solved permanently'];
        const learningTerms = ['iterate', 'improve', 'learn', 'adapt', 'update', 'experiment', 'feedback'];

        const hasStaticTerms = staticTerms.some(term => contentLower.includes(term.toLowerCase()));
        const hasLearningTerms = learningTerms.some(term => contentLower.includes(term.toLowerCase()));

        if (hasStaticTerms && !hasLearningTerms) {
          return {
            invariantNumber: invariant.number,
            invariantName: invariant.name,
            description: invariant.principle,
            violationReason: "Note presents a static solution without room for improvement",
            suggestion: "Incorporate mechanisms for feedback, learning, and continuous improvement in your approach."
          };
        }
        break;

      case 5: // 실용주의 (Business Impact First)
        // Check if the note focuses on theory without practical application
        const theoryTerms = ['theory', 'theoretical', 'academic', 'conceptual'];
        const practicalTerms = ['practical', 'application', 'implement', 'business value', 'ROI', 'impact', 'result'];

        const hasTheoryTerms = theoryTerms.some(term => contentLower.includes(term.toLowerCase()));
        const hasPracticalTerms = practicalTerms.some(term => contentLower.includes(term.toLowerCase()));

        if (hasTheoryTerms && !hasPracticalTerms) {
          return {
            invariantNumber: invariant.number,
            invariantName: invariant.name,
            description: invariant.principle,
            violationReason: "Note focuses on theory without practical business application",
            suggestion: "Connect theoretical concepts to practical business applications and measurable outcomes."
          };
        }
        break;
    }

    return null; // No violation found
  }

  /**
   * Calculates the average paragraph length in a note
   * @param content The note content
   * @returns number Average paragraph length in characters
   */
  private calculateAvgParagraphLength(content: string): number {
    const paragraphs = content.split(/\n\s*\n/);
    const totalLength = paragraphs.reduce((sum, p) => sum + p.length, 0);
    return totalLength / paragraphs.length;
  }

  /**
   * Displays a notice for invariant violations
   * @param violations Array of violations
   */
  showViolationsNotice(violations: InvariantViolation[]): void {
    if (violations.length === 0) return;

    const notice = new Notice(
      `⚠️ Note violates ${violations.length} invariant(s)`,
      10000 // 10 seconds
    );

    // Add a button to the notice to show details
    const noticeEl = notice.noticeEl;
    const detailsButton = noticeEl.createEl('button', {
      text: 'Show Details',
      cls: 'mod-cta'
    });

    detailsButton.addEventListener('click', () => {
      this.showViolationsModal(violations);
    });
  }

  /**
   * Shows a modal with detailed information about violations
   * @param violations Array of violations
   */
  private showViolationsModal(violations: InvariantViolation[]): void {
    // Use Obsidian API to create a modal
    const modal = new (class extends Modal {
      violations: InvariantViolation[];

      constructor(app: App, violations: InvariantViolation[]) {
        super(app);
        this.violations = violations;
      }

      onOpen() {
        const { contentEl } = this;

        contentEl.createEl('h2', { text: 'Invariant Violations' });

        for (const violation of this.violations) {
          const violationEl = contentEl.createDiv({ cls: 'invariant-violation' });

          violationEl.createEl('h3', { 
            text: `불변량 ${violation.invariantNumber}: ${violation.invariantName}`,
            cls: 'invariant-title'
          });

          violationEl.createEl('p', { 
            text: `원칙: ${violation.description}`,
            cls: 'invariant-principle'
          });

          violationEl.createEl('p', { 
            text: `위반 사항: ${violation.violationReason}`,
            cls: 'invariant-violation-reason'
          });

          violationEl.createEl('p', { 
            text: `제안: ${violation.suggestion}`,
            cls: 'invariant-suggestion'
          });
        }

        const buttonContainer = contentEl.createDiv({ cls: 'button-container' });

        const closeButton = buttonContainer.createEl('button', {
          text: 'Close',
          cls: 'mod-cta'
        });

        closeButton.addEventListener('click', () => {
          this.close();
        });
      }

      onClose() {
        const { contentEl } = this;
        contentEl.empty();
      }
    })(this.app, violations);

    modal.open();
  }
}

// Add Modal class definition for TypeScript
class Modal {
  app: App;
  contentEl: HTMLElement;

  constructor(app: App) {
    this.app = app;
    this.contentEl = document.createElement('div');
  }

  open() {
    // This would be implemented by Obsidian
  }

  close() {
    // This would be implemented by Obsidian
  }

  onOpen() {
    // Override in subclass
  }

  onClose() {
    // Override in subclass
  }
}

import { ItemView, WorkspaceLeaf, Notice, setIcon, TFile } from 'obsidian';
import NoteConnectorPlugin from './main';
import { NoteInfo, NoteSuggestion } from './types';

export const NOTE_CONNECTOR_VIEW_TYPE = 'note-connector-view';

export class NoteConnectorView extends ItemView {
  private plugin: NoteConnectorPlugin;
  private contentEl: HTMLElement;
  private isolatedNotes: NoteInfo[] = [];
  private singleNoteAnalysis: NoteInfo | null = null;
  private isLoading: boolean = false;
  private viewMode: 'isolated' | 'single' = 'isolated';

  constructor(leaf: WorkspaceLeaf, plugin: NoteConnectorPlugin) {
    super(leaf);
    this.plugin = plugin;
    this.contentEl = this.containerEl.children[1].createDiv({ cls: 'note-connector-view' });
  }

  getViewType(): string {
    return NOTE_CONNECTOR_VIEW_TYPE;
  }

  getDisplayText(): string {
    return 'Note Connector';
  }

  async onOpen(): Promise<void> {
    this.renderView();

    if (this.plugin.settings.scanOnStartup) {
      this.startScan();
    }
  }

  async onClose(): Promise<void> {
    // Nothing to clean up
  }

  /**
   * Renders the main view
   */
  renderView(): void {
    this.contentEl.empty();

    // Create header
    const headerEl = this.contentEl.createDiv({ cls: 'note-connector-header' });
    headerEl.createEl('h2', { text: 'Note Connector', cls: 'note-connector-title' });

    const buttonContainer = headerEl.createDiv({ cls: 'note-connector-buttons' });

    // Scan button
    const scanButton = buttonContainer.createEl('button', { 
      text: 'Scan for Isolated Notes',
      cls: 'note-connector-scan-button mod-cta'
    });
    scanButton.addEventListener('click', () => this.startScan());

    // Settings button
    const settingsButton = buttonContainer.createEl('button', { 
      cls: 'note-connector-settings-button'
    });
    setIcon(settingsButton, 'settings');
    settingsButton.addEventListener('click', () => {
      this.plugin.openSettingsTab();
    });

    // Content area
    if (this.isLoading) {
      this.renderLoadingState();
    } else if (this.viewMode === 'single' && this.singleNoteAnalysis) {
      this.renderSingleNoteAnalysis();
    } else if (this.isolatedNotes.length === 0) {
      this.renderEmptyState();
    } else {
      this.renderIsolatedNotes();
    }
  }

  /**
   * Renders the loading state
   */
  renderLoadingState(): void {
    const loadingEl = this.contentEl.createDiv({ cls: 'note-connector-loading' });
    loadingEl.createEl('span', { text: 'Scanning and analyzing notes...' });

    // Add a progress bar
    const progressContainer = this.contentEl.createDiv({ cls: 'note-connector-progress-container' });
    const progressEl = progressContainer.createEl('progress', { 
      cls: 'note-connector-progress',
      attr: { max: '100', value: '0' }
    });

    // Update progress periodically (simulated for now)
    let progress = 0;
    const progressInterval = setInterval(() => {
      progress += 5;
      if (progress > 100) progress = 0;
      progressEl.value = progress;

      if (!this.isLoading) {
        clearInterval(progressInterval);
        this.renderView();
      }
    }, 500);
  }

  /**
   * Renders the empty state
   */
  renderEmptyState(): void {
    const emptyEl = this.contentEl.createDiv({ cls: 'note-connector-empty-state' });
    emptyEl.createEl('p', { text: 'No isolated notes found in your vault.' });
    emptyEl.createEl('p', { text: 'Click "Scan for Isolated Notes" to start.' });
  }

  /**
   * Renders the list of isolated notes with suggestions
   */
  renderIsolatedNotes(): void {
    this.viewMode = 'isolated';
    const notesContainer = this.contentEl.createDiv({ cls: 'note-connector-notes-container' });

    // Summary
    notesContainer.createEl('p', { 
      text: `Found ${this.isolatedNotes.length} isolated notes in your vault.`,
      cls: 'note-connector-summary'
    });

    // Render each isolated note
    for (const note of this.isolatedNotes) {
      this.renderNoteWithSuggestions(notesContainer, note);
    }
  }

  /**
   * Renders a single note analysis
   */
  renderSingleNoteAnalysis(): void {
    if (!this.singleNoteAnalysis) return;

    this.viewMode = 'single';
    const container = this.contentEl.createDiv({ cls: 'note-connector-single-note-container' });

    // Add back button
    if (this.isolatedNotes.length > 0) {
      const backButton = container.createEl('button', {
        text: 'Back to Isolated Notes',
        cls: 'note-connector-back-button'
      });
      backButton.addEventListener('click', () => {
        this.viewMode = 'isolated';
        this.renderView();
      });
    }

    // Summary
    container.createEl('p', { 
      text: `Analyzing connections for "${this.singleNoteAnalysis.title}"`,
      cls: 'note-connector-summary'
    });

    // Render the note with its suggestions
    this.renderNoteWithSuggestions(container, this.singleNoteAnalysis);
  }

  /**
   * Renders a note with its suggestions
   * @param container The container element
   * @param note The note to render
   */
  private renderNoteWithSuggestions(container: HTMLElement, note: NoteInfo): void {
    const noteEl = container.createDiv({ cls: 'note-connector-isolated-note' });

    // Note title and path
    const titleEl = noteEl.createEl('div', { cls: 'note-connector-note-title' });
    titleEl.createEl('span', { text: note.title });

    // Add open button
    const openButton = titleEl.createEl('button', { 
      cls: 'note-connector-open-button',
      attr: { 'aria-label': 'Open note' }
    });
    setIcon(openButton, 'file-text');
    openButton.addEventListener('click', () => this.openNote(note.file));

    noteEl.createEl('div', { text: note.path, cls: 'note-connector-note-path' });

    // Suggestions section
    if (note.suggestions.length > 0) {
      const suggestionsEl = noteEl.createDiv({ cls: 'note-connector-suggestions' });
      suggestionsEl.createEl('div', { text: 'Suggested connections:', cls: 'note-connector-suggestions-header' });

      // Render each suggestion
      for (const suggestion of note.suggestions) {
        const suggestionEl = suggestionsEl.createDiv({ cls: 'note-connector-suggestion' });

        // Suggestion title
        suggestionEl.createEl('span', { 
          text: suggestion.title,
          cls: 'note-connector-suggestion-title'
        });

        // Relevance score
        suggestionEl.createEl('span', { 
          text: `${Math.round(suggestion.relevanceScore * 100)}%`,
          cls: 'note-connector-suggestion-score'
        });

        // Relevance reason
        suggestionEl.createEl('div', {
          text: suggestion.relevanceReason,
          cls: 'note-connector-suggestion-reason'
        });

        // Actions
        const actionsEl = suggestionEl.createDiv({ cls: 'note-connector-suggestion-actions' });

        // Connect button
        const connectButton = actionsEl.createEl('button', { 
          cls: 'note-connector-connect-button',
          attr: { 'aria-label': 'Connect notes' }
        });
        setIcon(connectButton, 'link');
        connectButton.addEventListener('click', () => this.connectNotes(note, suggestion));

        // Open button
        const openSuggestionButton = actionsEl.createEl('button', { 
          cls: 'note-connector-open-suggestion-button',
          attr: { 'aria-label': 'Open note' }
        });
        setIcon(openSuggestionButton, 'file-text');
        openSuggestionButton.addEventListener('click', () => this.openNote(suggestion.file));
      }
    } else {
      noteEl.createEl('div', { 
        text: 'No relevant connections found for this note.',
        cls: 'note-connector-no-suggestions'
      });
    }
  }

  /**
   * Shows the analysis for a single note
   * @param note The analyzed note to show
   */
  showAnalyzedNote(note: NoteInfo): void {
    this.singleNoteAnalysis = note;
    this.viewMode = 'single';
    this.renderView();
  }

  /**
   * Starts the scanning process
   */
  async startScan(): Promise<void> {
    this.isLoading = true;
    this.renderView();

    try {
      // Scan for isolated notes
      this.isolatedNotes = await this.plugin.scanForIsolatedNotes();
      new Notice(`Found ${this.isolatedNotes.length} isolated notes`);
      this.viewMode = 'isolated';
    } catch (error) {
      console.error('Error scanning for isolated notes:', error);
      new Notice('Error scanning for isolated notes');
    } finally {
      this.isLoading = false;
      this.renderView();
    }
  }

  /**
   * Opens a note in a new leaf
   * @param file The file to open
   */
  openNote(file: TFile): void {
    this.app.workspace.getLeaf(true).openFile(file);
  }

  /**
   * Connects two notes by creating links between them
   * @param sourceNote The source note
   * @param targetSuggestion The target note suggestion
   */
  async connectNotes(sourceNote: NoteInfo, targetSuggestion: NoteSuggestion): Promise<void> {
    try {
      const success = await this.plugin.connectNotes(sourceNote, targetSuggestion);

      if (success) {
        new Notice(`Connected "${sourceNote.title}" to "${targetSuggestion.title}"`);

        // Remove the suggestion from the list
        sourceNote.suggestions = sourceNote.suggestions.filter(
          suggestion => suggestion.path !== targetSuggestion.path
        );

        // Refresh the view
        this.renderView();
      } else {
        new Notice('Failed to connect notes');
      }
    } catch (error) {
      console.error('Error connecting notes:', error);
      new Notice('Error connecting notes');
    }
  }
}
