.vault-monitor-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
    border-bottom: 1px solid var(--background-modifier-border);
    padding-bottom: 10px;
}

.vault-monitor-header h2 {
    margin: 0;
    font-size: 1.3em;
}

