import { App, Plugin, PluginSettingTab, Setting, TFile, TAbstractFile, WorkspaceLeaf, Notice } from 'obsidian';
import { VaultMonitorView, VIEW_TYPE_VAULT_MONITOR } from './view';
import { VaultChangeTracker } from './vault-tracker';
import { VaultStats } from './vault-stats';

interface VaultMonitorSettings {
	enableNotifications: boolean;
	logRetentionDays: number;
}

const DEFAULT_SETTINGS: VaultMonitorSettings = {
	enableNotifications: true,
	logRetentionDays: 30
};

export default class VaultMonitorPlugin extends Plugin {
	settings: VaultMonitorSettings;
	vaultTracker: VaultChangeTracker;
	vaultStats: VaultStats;

	async onload() {
		await this.loadSettings();

		// Initialize components
		this.vaultStats = new VaultStats(this.app);
		this.vaultTracker = new VaultChangeTracker(this.app, this);

		// Register view
		this.registerView(
			VIEW_TYPE_VAULT_MONITOR,
			(leaf) => new VaultMonitorView(leaf, this)
		);

		// Add ribbon icon
		this.addRibbonIcon('activity-log', 'Vault Monitor', () => {
			this.activateView();
		});

		// Add settings tab
		this.addSettingTab(new VaultMonitorSettingTab(this.app, this));

		// Start tracking vault changes
		this.vaultTracker.startTracking();

		console.log('Vault Monitor plugin loaded');
	}

	onunload() {
		this.vaultTracker.stopTracking();
		console.log('Vault Monitor plugin unloaded');
	}

	async loadSettings() {
		this.settings = Object.assign({}, DEFAULT_SETTINGS, await this.loadData());
	}

	async saveSettings() {
		await this.saveData(this.settings);
	}

	async activateView() {
		const { workspace } = this.app;

		// Check if view already exists
		let leaf = workspace.getLeavesOfType(VIEW_TYPE_VAULT_MONITOR)[0];

		if (!leaf) {
			// Create new leaf in right sidebar
			leaf = workspace.getRightLeaf(false);
			await leaf.setViewState({
				type: VIEW_TYPE_VAULT_MONITOR,
				active: true,
			});
		}

		// Reveal the leaf
		workspace.revealLeaf(leaf);
	}

	showNotification(message: string) {
		if (this.settings.enableNotifications) {
			new Notice(message);
		}
	}
}

class VaultMonitorSettingTab extends PluginSettingTab {
	plugin: VaultMonitorPlugin;

	constructor(app: App, plugin: VaultMonitorPlugin) {
		super(app, plugin);
		this.plugin = plugin;
	}

	display(): void {
		const { containerEl } = this;

		containerEl.empty();

		containerEl.createEl('h2', { text: 'Vault Monitor Settings' });

		new Setting(containerEl)
			.setName('Enable Notifications')
			.setDesc('Show notifications when notes are added or deleted')
			.addToggle(toggle => toggle
				.setValue(this.plugin.settings.enableNotifications)
				.onChange(async (value) => {
					this.plugin.settings.enableNotifications = value;
					await this.plugin.saveSettings();
				}));

		new Setting(containerEl)
			.setName('Log Retention Period')
			.setDesc('Number of days to keep activity logs (1-365)')
			.addSlider(slider => slider
				.setLimits(1, 365, 1)
				.setValue(this.plugin.settings.logRetentionDays)
				.setDynamicTooltip()
				.onChange(async (value) => {
					this.plugin.settings.logRetentionDays = value;
					await this.plugin.saveSettings();
				}));
	}
}

import { App, TAbstractFile, TFile, Vault } from 'obsidian';
import VaultMonitorPlugin from './main';

export interface VaultChange {
    type: 'added' | 'deleted' | 'modified';
    filename: string;
    path: string;
    timestamp: number;
}

export class VaultChangeTracker {
    private app: App;
    private plugin: VaultMonitorPlugin;
    private recentChanges: VaultChange[] = [];
    private maxChanges = 1000; // Maximum number of changes to store in memory

    constructor(app: App, plugin: VaultMonitorPlugin) {
        this.app = app;
        this.plugin = plugin;
    }

    startTracking() {
        // Register event handlers for file changes
        this.registerFileEvents();
    }

    stopTracking() {
        // Nothing to do here as the event handlers are automatically removed when the plugin is unloaded
    }

    private registerFileEvents() {
        // File created event
        this.registerEvent(
            this.app.vault.on('create', (file) => {
                if (file instanceof TFile && file.extension === 'md') {
                    this.recordChange('added', file);
                }
            })
        );

        // File deleted event
        this.registerEvent(
            this.app.vault.on('delete', (file) => {
                if (file instanceof TFile && file.extension === 'md') {
                    this.recordChange('deleted', file);
                }
            })
        );

        // File modified event
        this.registerEvent(
            this.app.vault.on('modify', (file) => {
                if (file instanceof TFile && file.extension === 'md') {
                    this.recordChange('modified', file);
                }
            })
        );
    }

    private recordChange(type: 'added' | 'deleted' | 'modified', file: TAbstractFile) {
        const change: VaultChange = {
            type,
            filename: file.name,
            path: file.path,
            timestamp: Date.now()
        };

        // Add to recent changes
        this.recentChanges.unshift(change);

        // Trim the changes list if it gets too long
        if (this.recentChanges.length > this.maxChanges) {
            this.recentChanges = this.recentChanges.slice(0, this.maxChanges);
        }

        // Show notification for add/delete if enabled
        if (type === 'added' || type === 'deleted') {
            const message = `Note ${type}: ${file.name}`;
            this.plugin.showNotification(message);
        }

        // Trigger an update of the view if it's open
        this.app.workspace.trigger('vault-monitor:change-recorded');
    }

    getRecentChanges(limit?: number): VaultChange[] {
        if (limit) {
            return this.recentChanges.slice(0, limit);
        }
        return this.recentChanges;
    }

    // Clean up old changes based on retention period
    cleanupOldChanges() {
        const cutoffTime = Date.now() - (this.plugin.settings.logRetentionDays * 24 * 60 * 60 * 1000);
        this.recentChanges = this.recentChanges.filter(change => change.timestamp >= cutoffTime);
    }
}

import { ItemView, WorkspaceLeaf } from 'obsidian';
import VaultMonitorPlugin from './main';
import { VaultChange } from './vault-tracker';
import { VaultStatistics } from './vault-stats';

export const VIEW_TYPE_VAULT_MONITOR = 'vault-monitor-view';

export class VaultMonitorView extends ItemView {
    private plugin: VaultMonitorPlugin;
    private contentEl: HTMLElement;
    private statsEl: HTMLElement;
    private activityLogEl: HTMLElement;
    private refreshInterval: number;

    constructor(leaf: WorkspaceLeaf, plugin: VaultMonitorPlugin) {
        super(leaf);
        this.plugin = plugin;

        // Set up auto-refresh every 30 seconds
        this.refreshInterval = window.setInterval(() => {
            this.refreshView();
        }, 30000);
    }

    getViewType(): string {
        return VIEW_TYPE_VAULT_MONITOR;
    }

    getDisplayText(): string {
        return 'Vault Monitor';
    }

    getIcon(): string {
        return 'activity-log';
    }

    async onOpen(): Promise<void> {
        const { containerEl } = this;

        // Main container
        this.contentEl = containerEl.createDiv({ cls: 'vault-monitor-container' });

        // Create header
        const headerEl = this.contentEl.createEl('div', { cls: 'vault-monitor-header' });
        headerEl.createEl('h2', { text: 'Vault Monitor' });

        // Create refresh button
        const refreshBtn = headerEl.createEl('button', { 
            cls: 'vault-monitor-refresh-btn',
            text: 'Refresh'
        });
        refreshBtn.addEventListener('click', () => this.refreshView());

        // Create stats section
        const statsSection = this.contentEl.createEl('div', { cls: 'vault-monitor-section' });
        statsSection.createEl('h3', { text: 'Vault Statistics' });
        this.statsEl = statsSection.createDiv({ cls: 'vault-monitor-stats' });

        // Create activity log section
        const activitySection = this.contentEl.createEl('div', { cls: 'vault-monitor-section' });
        activitySection.createEl('h3', { text: 'Recent Activity' });
        this.activityLogEl = activitySection.createDiv({ cls: 'vault-monitor-activity-log' });

        // Register for change events
        this.registerEvent(
            this.app.workspace.on('vault-monitor:change-recorded', () => {
                this.refreshView();
            })
        );

        // Initial refresh
        await this.refreshView();
    }

    async onClose() {
        // Clear the refresh interval
        if (this.refreshInterval) {
            clearInterval(this.refreshInterval);
        }
    }

    async refreshView() {
        // Refresh stats
        await this.refreshStats();

        // Refresh activity log
        this.refreshActivityLog();
    }

    async refreshStats() {
        // Clear existing content
        this.statsEl.empty();

        // Get fresh stats
        const stats = await this.plugin.vaultStats.refreshStats();

        // Create stats grid
        const statsGrid = this.statsEl.createDiv({ cls: 'vault-monitor-stats-grid' });

        // Add stat items
        this.createStatItem(statsGrid, 'Total Notes', stats.totalNotes.toString());
        this.createStatItem(statsGrid, 'Total Links', stats.totalLinks.toString());
        this.createStatItem(statsGrid, 'Isolated Notes', stats.isolatedNotes.toString());

        // Add last updated info
        const lastUpdated = this.statsEl.createDiv({ cls: 'vault-monitor-last-updated' });
        lastUpdated.setText(`Last updated: ${new Date(stats.lastUpdated).toLocaleString()}`);
    }

    createStatItem(container: HTMLElement, label: string, value: string) {
        const statItem = container.createDiv({ cls: 'vault-monitor-stat-item' });
        statItem.createDiv({ cls: 'vault-monitor-stat-value', text: value });
        statItem.createDiv({ cls: 'vault-monitor-stat-label', text: label });
        return statItem;
    }

    refreshActivityLog() {
        // Clear existing content
        this.activityLogEl.empty();

        // Get recent changes (last 10)
        const recentChanges = this.plugin.vaultTracker.getRecentChanges(10);

        if (recentChanges.length === 0) {
            this.activityLogEl.createDiv({ 
                cls: 'vault-monitor-empty-log',
                text: 'No recent activity'
            });
            return;
        }

        // Create activity list
        const activityList = this.activityLogEl.createEl('ul', { cls: 'vault-monitor-activity-list' });

        // Add activity items
        recentChanges.forEach(change => {
            const item = activityList.createEl('li', { cls: 'vault-monitor-activity-item' });

            // Add icon based on change type
            const iconClass = this.getChangeTypeIcon(change.type);
            item.createSpan({ cls: `vault-monitor-activity-icon ${iconClass}` });

            // Add content
            const content = item.createDiv({ cls: 'vault-monitor-activity-content' });

            // Add title with change type and filename
            content.createDiv({ 
                cls: 'vault-monitor-activity-title',
                text: `${this.capitalizeFirstLetter(change.type)}: ${change.filename}`
            });

            // Add timestamp
            content.createDiv({ 
                cls: 'vault-monitor-activity-time',
                text: new Date(change.timestamp).toLocaleString()
            });
        });
    }

    getChangeTypeIcon(type: string): string {
        switch (type) {
            case 'added': return 'vault-monitor-icon-added';
            case 'deleted': return 'vault-monitor-icon-deleted';
            case 'modified': return 'vault-monitor-icon-modified';
            default: return '';
        }
    }

    capitalizeFirstLetter(string: string): string {
        return string.charAt(0).toUpperCase() + string.slice(1);
    }
}

import { App, TFile, getAllTags } from 'obsidian';

export interface VaultStatistics {
    totalNotes: number;
    totalLinks: number;
    isolatedNotes: number;
    lastUpdated: number;
}

export class VaultStats {
    private app: App;
    private stats: VaultStatistics;

    constructor(app: App) {
        this.app = app;
        this.stats = {
            totalNotes: 0,
            totalLinks: 0,
            isolatedNotes: 0,
            lastUpdated: 0
        };
    }

    async refreshStats(): Promise<VaultStatistics> {
        const markdownFiles = this.app.vault.getMarkdownFiles();
        const { metadataCache } = this.app;

        let totalLinks = 0;
        let isolatedNotes = 0;

        // Count notes with no links
        for (const file of markdownFiles) {
            const cache = metadataCache.getFileCache(file);

            // Count outgoing links
            const outgoingLinks = cache?.links?.length || 0;

            // Count incoming links (backlinks)
            const backlinks = Object.values(metadataCache.resolvedLinks[file.path] || {}).reduce((sum, count) => sum + count, 0);

            totalLinks += outgoingLinks;

            // A note is isolated if it has no outgoing or incoming links
            if (outgoingLinks === 0 && backlinks === 0) {
                isolatedNotes++;
            }
        }

        this.stats = {
            totalNotes: markdownFiles.length,
            totalLinks: totalLinks,
            isolatedNotes: isolatedNotes,
            lastUpdated: Date.now()
        };

        return this.stats;
    }

    getStats(): VaultStatistics {
        return this.stats;
    }

    // Format a timestamp as a readable date/time string
    static formatTimestamp(timestamp: number): string {
        return new Date(timestamp).toLocaleString();
    }
}

